CREATE DEFINER=`root`@`%` PROCEDURE `ottieniCronologia` (IN `user` INT, IN `numero_limite` INT)  BEGIN
	SELECT 	
			I.Nome,
			I.Tipo,
            I.Livello,
            I.TempoDiLancio,
            I.Componenti,
            I.Durata,
            I.Gittata,
			I.Descrizione,
            CI.NomeClasse,
            I.Manuale,
            max(G.Timestamp) as data
    FROM cronologia G 
    INNER JOIN incantesimi I ON G.NomeIncantesimo = I.Nome
    INNER JOIN classeincantesimi CI ON I.Nome = CI.NomeIncantesimo 
    WHERE G.IdUser = user
    GROUP BY I.Nome
    ORDER BY data desc
    LIMIT numero_limite;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `ottieniIncantesimiDiLivello` (IN `livello` INT)  BEGIN
	SELECT 	I.Nome,
			I.Tipo,
            I.Livello,
            I.TempoDiLancio,
            I.Componenti,
            I.Durata,
            I.Gittata,
			I.Descrizione,
            group_concat(CI.NomeClasse) as Classe,
            I.Manuale
    FROM 	incantesimi I INNER JOIN classeincantesimi CI ON I.Nome = CI.NomeIncantesimo
    WHERE I.Livello  = livello
    GROUP BY I.Nome
    ORDER BY I.Nome;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `ottieniIncantesimiPerClasse` (IN `classe` VARCHAR(45))  BEGIN
	SELECT 	I.Nome,
			I.Tipo,
            I.Livello,
            I.TempoDiLancio,
            I.Componenti,
            I.Durata,
            I.Gittata,
			I.Descrizione,
            group_concat(CI.NomeClasse) as Classe,
            I.Manuale
    FROM incantesimi I INNER JOIN classeincantesimi CI ON I.Nome = CI.NomeIncantesimo
    WHERE CI.NomeClasse = classe
    GROUP BY I.Nome
    ORDER BY I.Livello, I.Nome;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `ottieniIncantesimiPerClasseDiLivello` (IN `classe` VARCHAR(45), IN `livello` INT)  BEGIN
	SELECT 	I.Nome,
			I.Tipo,
            I.Livello,
            I.TempoDiLancio,
            I.Componenti,
            I.Durata,
            I.Gittata,
			I.Descrizione,
            CI.NomeClasse,
            I.Manuale
    FROM incantesimi I INNER JOIN classeincantesimi CI ON I.Nome = CI.NomeIncantesimo 
    WHERE CI.NomeClasse = classe and I.Livello = livello
    ORDER BY I.livello,I.Nome;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `ottieniIncantesimiPerNome` (IN `nome` VARCHAR(100))  BEGIN
	SELECT 	I.Nome,
			I.Tipo,
            I.Livello,
            I.TempoDiLancio,
            I.Componenti,
            I.Durata,
            I.Gittata,
			I.Descrizione,
            group_concat(CI.NomeClasse) as Classe,
            I.Manuale
    FROM incantesimi I INNER JOIN classeincantesimi CI ON I.Nome = CI.NomeIncantesimo
    WHERE I.nome like CONCAT('%', nome , '%')
    GROUP BY I.Nome;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `ottieniPreferiti` (IN `utente` INT)  BEGIN
	SELECT 	I.Nome,
			I.Tipo,
            I.Livello,
            I.TempoDiLancio,
            I.Componenti,
            I.Durata,
            I.Gittata,
			I.Descrizione,
            group_concat(CI.NomeClasse) as Classe
    FROM 	incantesimi I INNER JOIN classeincantesimi CI ON I.Nome = CI.NomeIncantesimo 
			INNER JOIN preferiti P ON I.Nome = P.NomeIncantesimo INNER JOIN
			(
				SELECT * 
				FROM user U
                WHERE U.IdUser = utente
            ) as D ON D.IdUser = P.IdUser
	ORDER BY I.Nome;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `rimuoviPreferiti` (IN `utente` INT, IN `incantesimo` VARCHAR(100))  BEGIN
	DELETE 	FROM preferiti
    WHERE IdUser = utente AND NomeIncantesimo = incantesimo;
END$$

